extern struct Library * xadMasterBase;

void __init_xadmaster_main(void) __attribute__((constructor));
void __exit_xadmaster_main(void) __attribute__((destructor));

/****************************************************************************/

void __init_xadmaster_main(void)
{
    struct Library * LibBase;
    if (xadMasterBase == NULL) /* Library base is NULL, we need to open it */
    {
        LibBase = IExec->OpenLibrary("xadmaster.library", 0L);
        assert(LibBase != NULL);
    }
    else
    {
        LibBase = xadMasterBase;
    }

    IxadMaster = (struct xadMasterIFace *)IExec->GetInterface((struct Library *)LibBase, "main", 1, NULL);
    assert(IxadMaster != NULL);
}

/****************************************************************************/

void __exit_xadmaster_main(void)
{
    if (IxadMaster) IExec->DropInterface ((struct Interface *)IxadMaster);
}

