extern struct Library * LayersBase;

void __init_layers_main(void) __attribute__((constructor));
void __exit_layers_main(void) __attribute__((destructor));

/****************************************************************************/

void __init_layers_main(void)
{
    struct Library * LibBase;
    if (LayersBase == NULL) /* Library base is NULL, we need to open it */
    {
        LibBase = IExec->OpenLibrary("layers.library", 0L);
        assert(LibBase != NULL);
    }
    else
    {
        LibBase = LayersBase;
    }

    ILayers = (struct LayersIFace *)IExec->GetInterface((struct Library *)LibBase, "main", 1, NULL);
    assert(ILayers != NULL);
}

/****************************************************************************/

void __exit_layers_main(void)
{
    if (ILayers) IExec->DropInterface ((struct Interface *)ILayers);
}

